import React, {useState} from 'react';
import Style from 'styled-components';
import xout from './xout.png'



const DIV = Style.div`
width: 450px;
border: 1px solid white;
padding: .5%;
`

const DIV2 = Style.div`
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;`

const H1 = Style.h4`
margin-left: 1%;
margin-right: 1%;
color: white;
`
const H4 = Style.h4`
color: white;
margin: 0;
font-weight: 200;



`
const DIV4 = Style.div`
background-color: blue;
opacity: 60%;
margin-bottom:15px;
width: 200px;
padding: 3%;
display: flex;
flex-direction: row;
justify-content: space-between;
`


const DIV3 = Style.div`

`

const CREATE = Style.div`
margin-top: 20px;
display: flex;
justify-content: center;


`

const FORM = Style.form`
display: flex;
flex-direction: column;
align-items: center;


`

const INPUT = Style.input`
width: 50px;
border: 1px solid white;
border-radius: 5px;
height: 20px;
`


const DIV1 = Style.div`
`

const HEADER = Style.div`
display: flex;
font - family: 'Source Sans Pro', sans - serif;
font-size: 2.5rem;
font-weight: 700;
color: #009a9a;

`

const BUTTON1 = Style.button`
width: 150px;
background-color: teal;
border: none;
color: white;
border-radius: 7px;
height: 40px;
margin-bottom:15px;
`

const INPUT2 = Style.input`
width: 190px;
height: 30px;
`


//fonts 
//

// font - family: 'Poppins', sans - serif;---- regular fonts

// font - family: 'Source Sans Pro', sans - serif;




const CreateTimer = (props) => {

    const [time, setTime] = useState({
        year: '',
        month: '',
        day: '',
        description: ''
    })

    const [display, setDisplay] = useState('no-display')

    

    const submitHandler = e => {
            e.preventDefault()

        // defining my current date
        let dateObj = new Date()
        let dateday = dateObj.getUTCDate();
        let dateyear = dateObj.getUTCFullYear();
        let datemonth = dateObj.getUTCMonth() + 1;

        //defining the date the user entered
        let day = Number(time.day)
        let month = Number(time.month)
        let year = Number(time.year)




        if (year > dateyear || (year === dateyear && month > datemonth) || (year === dateyear && month === datemonth && day > dateday)) {
            let newTime = {
                ...time,
                id: Date.now()
            }
            props.addnewTime(newTime)
        } else {
            setDisplay('no-display')
        }
    }




    const changeHandler = e => {
        e.preventDefault();
        return setTime({
            ...time,
            [e.target.name] : e.target.value
        })

        
    }

    // const hideBox = e => {
    //     e.preventDefault();
    //     setDisplay('no-display')
    // }



    return (

        <DIV>

            <HEADER >
                <DIV1>Countdown</DIV1>
            </HEADER>




            <CREATE>
                <FORM onSubmit={submitHandler}>


                        <DIV3>
                            <INPUT2 className='text-area' type='text' placeholder='Event' onChange={changeHandler} name='description' />
                        </DIV3>



                        <DIV2>

                            <INPUT type='text' placeholder='month' onChange={changeHandler} name='month'/>
                            <H1>/</H1>
                            <INPUT type='text' placeholder='day' onChange={changeHandler} name='day'/>
                            <H1>/</H1>
                            <INPUT type='text' placeholder='year' onChange={changeHandler} name='year'/>

                           

                        </DIV2>

                        <DIV4 className={display}>
                            <H4>Please Enter a future date</H4>
                            <img  onClick={hideBox} src={xout} className='delete-photo'/>

                        </DIV4>


                 
                        <BUTTON1 type='submit'>Create Event</BUTTON1>


                </FORM>

            </CREATE>


        </DIV>
    )
}



export default CreateTimer;
