import React, {useState} from 'react';
import Style from 'styled-components';



const DIV = Style.div`
// background-color: #0b0f54;
width: 450px;
border: 1px solid white;
height: 250px;
padding: .5%;
`

const DIV2 = Style.div`
display: flex;
flex-direction: row;
justify-content: center;
align-items: center;`

const H1 = Style.h4`
margin-left: 1%;
margin-right: 1%;
color: white;
`
const DIV3 = Style.div`

`

const CREATE = Style.div`
margin-top: 20px;
display: flex;
justify-content: center;


`

const FORM = Style.form`
display: flex;
flex-direction: column;
// height: 200px;
align-items: center;


`

const INPUT = Style.input`
width: 50px;
border: 1px solid white;
border-radius: 5px;
height: 20px;
`


const DIV1 = Style.div`
`

const HEADER = Style.div`
display: flex;
font - family: 'Source Sans Pro', sans - serif;
font-size: 2.5rem;
font-weight: 700;
color: #009a9a;

`

const BUTTON1 = Style.button`
width: 150px;
background-color: teal;
border: none;
color: white;
border-radius: 7px;
height: 40px;
`

const INPUT2 = Style.input`
width: 190px;
height: 30px;
`

//fonts 
//

// font - family: 'Poppins', sans - serif;---- regular fonts

// font - family: 'Source Sans Pro', sans - serif;




const CreateTimer = (props) => {

    const [time, setTime] = useState({
        year: '',
        month: '',
        day: '',
        description: ''
    })


    const submitHandler = e => {

        // defining my current date
        let dateObj = new Date()
        let dateday = dateObj.getUTCDate();
        let dateyear = dateObj.getUTCFullYear();
        let datemonth = dateObj.getUTCMonth();

        let currentTotalDays = (dateyear * 365)

        //defining the date the user entered

        let day = time.day
        let month = time.month
        let year = time.year

        let userTotalDays = (year*365)

        if (year > dateyear || (year == dateyear && month > datemonth) || ((year == dateyear) && (month == datemonth) && (day > dateday))){
            e.preventDefault()
            let newTime = {
                ...time,
                id: Date.now()
            }
            props.addnewTime(newTime)
        } else {
            alert('Please enter a date in the future')
        }
    }

    const changeHandler = e => {
        e.preventDefault();
        return setTime({
            ...time,
            [e.target.name] : e.target.value
        })

        
    }




    return (

        <DIV>

            <HEADER>
                <DIV1>Countdown</DIV1>
            </HEADER>




            <CREATE>
                <FORM onSubmit={submitHandler}>


                        <DIV3>
                            <INPUT2 className='text-area' type='text' placeholder='Event' onChange={changeHandler} name='description' />
                        </DIV3>



                        <DIV2>

                            <INPUT type='text' placeholder='month' onChange={changeHandler} name='month'/>
                            <H1>/</H1>
                            <INPUT type='text' placeholder='day' onChange={changeHandler} name='day'/>
                            <H1>/</H1>
                            <INPUT type='text' placeholder='year' onChange={changeHandler} name='year'/>

                           

                        </DIV2>


                 
                        <BUTTON1 type='submit'>Create Event</BUTTON1>


                </FORM>

            </CREATE>


        </DIV>
    )
}



export default CreateTimer;
